export default {
    state: {
        notes: [{
                note: {
                    priority: "Low",
                    options: [
                        { id: 1, value: 'Low' },
                        { id: 2, value: 'Medium' },
                        { id: 3, value: 'High' }
                    ]
                },

                description: {
                    descrContent: "Description for first note",
                    descrEditing: 'descrEditing',
                    hidenDescr: false,
                    descrShow: true,
                },
                title: {
                    nameOfNote: "First note",
                    hidenTitle: false,
                    titleShow: true,
                    additionalTitleVar: "",
                },
                date: new Date(Date.now()).toLocaleString()
            },
            {
                note: {
                    priority: "Low",
                    options: [
                        { id: 1, value: 'Low' },
                        { id: 2, value: 'Medium' },
                        { id: 3, value: 'High' }
                    ]
                },

                description: {
                    descrContent: "Description for second note",
                    descrEditing: 'descrEditing',
                    hidenDescr: false,
                    descrShow: true,
                },
                title: {
                    nameOfNote: "Second note",
                    hidenTitle: false,
                    titleShow: true,
                    additionalTitleVar: "",
                },
                date: new Date(Date.now()).toLocaleString()
            },
            {
                note: {
                    priority: "Low",
                    options: [
                        { id: 1, value: 'Low' },
                        { id: 2, value: 'Medium' },
                        { id: 3, value: 'High' }
                    ]
                },

                description: {
                    descrContent: "Description for Third note",
                    descrEditing: 'descrEditing',
                    hidenDescr: false,
                    descrShow: true,
                },
                title: {
                    nameOfNote: "Third note",
                    hidenTitle: false,
                    titleShow: true,
                    additionalTitleVar: "",
                },
                date: new Date(Date.now()).toLocaleString()
            },
        ],

        grid: true

    },

    mutations: {

        addNote(state, {...note }) {
            state.notes.push({
                title: {...note.title },
                note: {...note.note },
                description: {...note.description },
                date: note.date
            })
        },
        setNotes(state, notes) {
            state.notes = notes
        },
        removeNote(state, note) {
            state.notes.splice(note, 1)
        },
        changeGrid(state, boolean) {
            state.grid = boolean
        }



    },

    actions: {
        addNote({ commit }, {...note }) {
            commit('addNote', {...note })
        },
        setNotes({ commit }, notes) {
            commit('setNotes', notes)
        },
        removeNote({ commit }, note) {
            commit('removeNote', note)
        },
        changeGrid({ commit }, boolean) {
            commit('changeGrid', boolean)
        },

    },
    getters: {
        getNotes: state => state.notes,

        getGrid: state => state.grid
    },
}
